import axios from 'axios';
import qs from 'qs';


type LoginResponse = {
  "access_token": string,
  "token_type": string,
  "refresh_token": "eyJhbGciOiJIUzI1NiIsInR5cCI6IkpXVCJ9..QY1m-BOwrUWNCMk4toN7eF5zJcfeuGQviw21M82pwOA",
  "expires_in": 86399,
  "scope": "read write",
  "userFirstName": "Maria",
  "userId": 2
}

export const BASE_URL =
  process.env.REACT_APP_BACKEND_URL ?? 'http://localhost:8080';

export const CLIENT_ID = process.env.REACT_APP_CLIENT_ID ?? 'dscatalog';

export const CLIENT_SECRET =
  process.env.REACT_APP_CLIENT_SECRET ?? 'dscatalog123';

const basicHeader = () => {
  return `Basic ${btoa(`${CLIENT_ID}:${CLIENT_SECRET}`)}`;
};

type LoginData = {
  username: string;
  password: string;
};

export const requestBackendLogin = (loginData: LoginData) => {
  const headers = {
    'Content-Type': 'application/x-www-form-urlencoded',
    Authorization: basicHeader(),
  };
  const data = qs.stringify({
    ...loginData,
    grant_type: 'password',
  });

  return axios({
    method: 'POST',
    baseURL: BASE_URL,
    url: '/oauth/token',
    data,
    headers,
  });
};
